// react class component: rcc
import React, { Component } from 'react'
//imrd
import ReactDOM from 'react-dom'
export default class EstacaoClimatica extends Component {
  render() {
    return (
        <div className='card'>
                            <div className='card-body'>
                                <div 
                                    style={{height: '6rem'}}
                                    className='d-flex align-items-center rounded md-2'>
                                        <i className={'fa-solid fa-5x ${this.state.icone}'}></i>
                                        <p className='w-75 ms-3 text-center fs -1'>{this.estacao}</p>
                                </div>
                                <p className='text-center'>
                                    {
                                        this.state.latitude ?
                                            'Coordenadas: ${this.state.latitude},${this.state.longitude}. Data: ${this.state.data}'
                                        :
                                            this.state.menssagemDeErro ?
                                                this.state.menssagemDeErro
                                            :
                                            'Clique no botão para saber a sua estação climática'
                                    }
                                </p>
                                <button
                                    onClick={this.obterLocalizacao}
                                    className='btn btn-outline-primary w-100 mt-2'>
                                    Qual a minha estação?
                                </button>

                                <button
                                    className="btn btn-outline-danger w-100 mt-2"
                                    onClick={() => {
                                        ReactDOM.unmountComponentAtNode(
                                            document.querySelector('#root')
                                        )
                                    }}>

                                </button>
                            </div>
                        </div>
    )
  }
}
