    }

    icones = {
        'Primavera': 'fa-sedding',
        'Verão': 'fa-sun',
        'Outuno': 'fa-tree',
        'Inverno': 'fa-snowflake'
    }


    render(){
        return (
            <div>
                Meu app
            </div>
        )
    }
}

ReactDOM.render(
    <App />,
    document.querySelector('#root')
)
